var testEl = document.createElement( "video" ),
    mpeg4, webm;
if ( testEl.canPlayType ) {
    mpeg4 = "" !== testEl.canPlayType( 'video/mp4; codecs="mp4v.20.8"' );
    webm = "" !== testEl.canPlayType( 'video/webm; codecs="vp8, vorbis"' );
}


(function($) {
  $.fn.visible = function(partial) {
    
      var $t            = $(this),
          $w            = $(window),
          viewTop       = $w.scrollTop(),
          viewBottom    = viewTop + $w.height(),
          _top          = $t.offset().top,
          _bottom       = _top + $t.height(),
          compareTop    = partial === true ? _bottom : _top,
          compareBottom = partial === true ? _top : _bottom;
    
    return ((compareBottom <= viewBottom) && (compareTop >= viewTop));

  };
    
})(jQuery);


(function() {

    var mp3 = 'data:audio/mpeg;base64,/+MYxAAAAANIAUAAAASEEB/jwOFM/0MM/90b/+RhST//w4NFwOjf///PZu////9lns5GFDv//l9GlUIEEIAAAgIg8Ir/JGq3/+MYxDsLIj5QMYcoAP0dv9HIjUcH//yYSg+CIbkGP//8w0bLVjUP///3Z0x5QCAv/yLjwtGKTEFNRTMuOTeqqqqqqqqqqqqq/+MYxEkNmdJkUYc4AKqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqq';

    var ogg = 'data:audio/ogg;base64,';

    try {
        var audio = new Audio();
        var src = audio.canPlayType('audio/ogg') ? ogg : mp3;
        audio.autoplay = true;
        audio.volume = 0;

        // this will only be triggered if autoplay works
        $(audio).on('play', function() {
            if(mpeg4 || webm){
                $(".video-container .poster").remove();    
            }else{
                $(".video-container video").remove();
                $(".video-container .poster").show();
           
            }
                
        });

        audio.src = src;
            
    } catch(e) {
        $(".video-container video").remove();
        $(".video-container .poster").show();
        console.log('[AUTOPLAY-ERROR]', e);
    }
})();





$( document ).ready(function() {
    scaleVideoContainer();
    initBannerVideoSize('.video-container .poster');
    initBannerVideoSize('.video-container .filter');
    initBannerVideoSize('.video-container video');
    var timeoutSet = null;
    var win = $(window);

    var all_sections = $("body div.container > div, body div.left-section > *, body div.right-section > *");

    all_sections.each(function(i, el) {
      var el = $(el);
      if (el.visible(true)) {
        el.addClass("section-slide-up"); 
      } 
    });


    win.on('resize', function() {
        scaleVideoContainer();
        scaleBannerVideoSize('.video-container .poster img');
        scaleBannerVideoSize('.video-container .filter');
        scaleBannerVideoSize('.video-container video'); 
    });

    $('.nav_icon').click(function(){
        $(this).toggleClass('open');
        $(".header_container ul").stop().slideToggle("fast");
    });

    win.scroll(function(){
        var st = win.scrollTop();
        $(".header_nav li").each(function(){
            var id = $(this).find('a').eq(0).attr('href');
            var scrollOffset = $(id).offset().top;
            var height = $(id).outerHeight();
            if(st>=scrollOffset && st < scrollOffset+height){
                $(id).addClass('section_visited');
                $(".header_nav li").removeClass('active');
                $(this).addClass('active');

            }
        });

        all_sections.each(function(i, el) {
            var el = $(el);
            if (el.visible(true)) {
              el.addClass("section-slide-up"); 
            } 
        });
    });

    $(".header_nav li a").click(function(e){
        e.preventDefault();
        var id = $(this).attr('href');
        scrollToSection(id);
    });



    $(window).scrollTop(0);

});

function scrollToSection(secion_id){
    $('body,html').stop().animate({
        scrollTop: $(secion_id).offset().top
    },500);
}

function scaleVideoContainer() {

    var height = $(window).height() + 5;
    var unitHeight = parseInt(height) + 'px';
    $('.homepage-hero-module').css('height',unitHeight);
    $('.homepage-hero-module').css('width',$(window).width());
    

}

function initBannerVideoSize(element){

    $(element).each(function(){
        $(this).data('height', $(this).height());
        $(this).data('width', $(this).width());
    });

    scaleBannerVideoSize(element);

}

function scaleBannerVideoSize(element){

    var windowWidth = $(window).width(),
    windowHeight = $(window).height() + 5,
    videoWidth,
    videoHeight;

    $(element).each(function(){
        var videoAspectRatio = $(this).data('height')/$(this).data('width');

        $(this).width(windowWidth);

        if(windowWidth < 1000){
            videoHeight = windowHeight;
            videoWidth = videoHeight / videoAspectRatio;
            $(this).css({'margin-top' : 0, 'margin-left' : -(videoWidth - windowWidth) / 2 + 'px'});

            $(this).width(videoWidth).height(videoHeight);
        }

        $('.homepage-hero-module .video-container video').addClass('fadeIn animated');

    });
}